import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LoadingSpinner from '../components/LoadingSpinner';
import ErrorMessage from '../components/ErrorMessage';

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    imageUrl: '',
    price: '',
    tags: '',
    isEcoFriendly: false
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (err) {
      setError('Failed to load products');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        tags: formData.tags.split(',').map(tag => tag.trim()).filter(tag => tag)
      };

      if (editingProduct) {
        // Update existing product (simulated)
        const updatedProducts = products.map(p => 
          p._id === editingProduct._id ? { ...p, ...productData } : p
        );
        setProducts(updatedProducts);
      } else {
        // Add new product (simulated)
        const newProduct = {
          _id: Date.now().toString(),
          ...productData
        };
        setProducts([newProduct, ...products]);
      }

      resetForm();
    } catch (err) {
      setError('Failed to save product');
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setFormData({
      name: product.name,
      description: product.description,
      imageUrl: product.imageUrl,
      price: product.price.toString(),
      tags: product.tags?.join(', ') || '',
      isEcoFriendly: product.isEcoFriendly
    });
    setShowAddForm(true);
  };

  const handleDelete = (productId) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      setProducts(products.filter(p => p._id !== productId));
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      imageUrl: '',
      price: '',
      tags: '',
      isEcoFriendly: false
    });
    setEditingProduct(null);
    setShowAddForm(false);
  };

  if (loading) {
    return <LoadingSpinner size="large" message="Loading admin panel..." fullScreen />;
  }

  if (error) {
    return <ErrorMessage title="Admin Panel Error" message={error} onRetry={fetchProducts} />;
  }

  return (
    <main>
      <div className="admin-container">
        <div className="admin-header">
          <h1>
            <i className="fas fa-cog"></i>
            Product Management
          </h1>
          <button 
            onClick={() => setShowAddForm(true)}
            className="add-product-btn"
          >
            <i className="fas fa-plus"></i>
            Add New Product
          </button>
        </div>

        {showAddForm && (
          <div className="product-form-modal">
            <div className="modal-content">
              <div className="modal-header">
                <h2>{editingProduct ? 'Edit Product' : 'Add New Product'}</h2>
                <button onClick={resetForm} className="close-btn">
                  <i className="fas fa-times"></i>
                </button>
              </div>

              <form onSubmit={handleSubmit} className="product-form">
                <div className="form-row">
                  <div className="form-group">
                    <label htmlFor="name">Product Name *</label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label htmlFor="price">Price *</label>
                    <input
                      type="number"
                      id="price"
                      name="price"
                      value={formData.price}
                      onChange={handleInputChange}
                      step="0.01"
                      min="0"
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label htmlFor="description">Description</label>
                  <textarea
                    id="description"
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    rows="3"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="imageUrl">Image URL</label>
                  <input
                    type="url"
                    id="imageUrl"
                    name="imageUrl"
                    value={formData.imageUrl}
                    onChange={handleInputChange}
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="tags">Tags (comma-separated)</label>
                  <input
                    type="text"
                    id="tags"
                    name="tags"
                    value={formData.tags}
                    onChange={handleInputChange}
                    placeholder="eco-friendly, sustainable, organic"
                  />
                </div>

                <div className="form-group">
                  <label className="checkbox-label">
                    <input
                      type="checkbox"
                      name="isEcoFriendly"
                      checked={formData.isEcoFriendly}
                      onChange={handleInputChange}
                    />
                    <span className="checkmark"></span>
                    Eco-Friendly Product
                  </label>
                </div>

                <div className="form-actions">
                  <button type="button" onClick={resetForm} className="cancel-btn">
                    Cancel
                  </button>
                  <button type="submit" className="save-btn">
                    <i className="fas fa-save"></i>
                    {editingProduct ? 'Update Product' : 'Add Product'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        <div className="products-table">
          <div className="table-header">
            <h3>All Products ({products.length})</h3>
          </div>

          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Image</th>
                  <th>Name</th>
                  <th>Price</th>
                  <th>Eco-Friendly</th>
                  <th>Tags</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.map(product => (
                  <tr key={product._id}>
                    <td>
                      <img 
                        src={product.imageUrl} 
                        alt={product.name}
                        className="product-thumbnail"
                      />
                    </td>
                    <td>
                      <div className="product-name">{product.name}</div>
                      <div className="product-description">
                        {product.description?.substring(0, 50)}...
                      </div>
                    </td>
                    <td className="price-cell">${product.price}</td>
                    <td>
                      {product.isEcoFriendly ? (
                        <span className="eco-badge">
                          <i className="fas fa-leaf"></i>
                          Yes
                        </span>
                      ) : (
                        <span className="not-eco">No</span>
                      )}
                    </td>
                    <td>
                      <div className="tags-cell">
                        {product.tags?.slice(0, 2).map((tag, index) => (
                          <span key={index} className="tag">{tag}</span>
                        ))}
                        {product.tags?.length > 2 && (
                          <span className="tag-more">+{product.tags.length - 2}</span>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          onClick={() => handleEdit(product)}
                          className="edit-btn"
                          title="Edit product"
                        >
                          <i className="fas fa-edit"></i>
                        </button>
                        <button 
                          onClick={() => handleDelete(product._id)}
                          className="delete-btn"
                          title="Delete product"
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

