import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import { WishlistContext } from '../context/WishlistContext';
import ProductReviews from '../components/ProductReviews';
import axios from 'axios';

function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  const { addToCart } = useContext(CartContext);
  const { wishlist, addToWishlist, removeFromWishlist } = useContext(WishlistContext) || { wishlist: [], addToWishlist: () => {}, removeFromWishlist: () => {} };

  const isInWishlist = wishlist.some(item => item._id === product?._id);

  useEffect(() => {
    async function fetchProduct() {
      try {
        const response = await axios.get(`http://localhost:5000/api/products/${id}`);
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product:', error);
      } finally {
        setLoading(false);
      }
    }

    fetchProduct();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (!product) return <div>Product not found</div>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <div style={{ display: 'flex', gap: '30px', flexWrap: 'wrap' }}>
        <div style={{ flex: '1', minWidth: '300px' }}>
          <img 
            src={product.imageUrl || '/placeholder-image.jpg'} 
            alt={product.name}
            style={{ width: '100%', maxWidth: '400px', height: 'auto' }}
          />
        </div>
        <div style={{ flex: '1', minWidth: '300px' }}>
          <h1>{product.name}</h1>
          <p style={{ fontSize: '24px', color: '#28a745', fontWeight: 'bold' }}>
            ${product.price}
          </p>
          {product.isEcoFriendly && (
            <div style={{ color: '#28a745', marginBottom: '10px' }}>
              ðŸŒ± Eco-Friendly Product
            </div>
          )}
          <p style={{ marginBottom: '20px' }}>{product.description}</p>
          {product.tags && product.tags.length > 0 && (
            <div style={{ marginBottom: '20px' }}>
              <strong>Tags: </strong>
              {product.tags.map((tag, index) => (
                <span key={index} style={{ 
                  backgroundColor: '#e9ecef', 
                  padding: '4px 8px', 
                  borderRadius: '4px', 
                  marginRight: '5px' 
                }}>
                  {tag}
                </span>
              ))}
            </div>
          )}
          <button 
            onClick={() => addToCart(product)}
            style={{
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              padding: '12px 24px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

export default ProductPage;
